mapper.select:
select * from product


        and goods_name like concat('%',#{goodsName},'%')


        and goods_sell_status=#{goodsSellStatus}


        and create_time>=#{startDate}


        and create_time<=#{endDate}


order by create_time desc

    limit #{start},#{limit}

select count(*) from product


    and goods_name like concat('%',#{goodsName},'%')


    and goods_sell_status=#{goodsSellStatus}


    and create_time>=#{startDate}


    and create_time<=#{endDate}
